using UnityEngine;

public class FirstPersonController : MonoBehaviour
{
    public float speed = 5.0f;
    public Rigidbody rb;

    // Update is called once per frame
    void Update()
    {
        float horizontalInput = Input.GetAxis("Horizontal");
        float verticalInput = Input.GetAxis("Vertical");

        // Calculate the new horizontal and vertical movement based on the inputs
        float newHorizontal = horizontalInput * speed * Time.deltaTime;
        float newVertical = verticalInput * speed * Time.deltaTime;

        // Set the new camera movement
        rb.transform.position += new Vector3(newHorizontal, 0, newVertical);
    }
}
