    <div class="scroll-container" ref="scrollContainer">
        <!-- Your scrollable content here -->
    </div>
    </template>

    <script>
    import { reactive, ref, onMounted } from 'vue';

    export default {
        setup() {
            const state = reactive({
                page: 1, // The current page
                isIntersecting: false, // Flag to determine if the target element is in view
            });

            const scrollContainer = ref(null); // Create a ref for the scroll container

            const observer = new IntersectionObserver(([entry]) => {
                state.isIntersecting = entry.isIntersecting; // Update the isIntersecting flag
            });

            onMounted(() => {
                observer.observe(scrollContainer.value); // Start observing the scroll container
            });

            // Function to handle the infinite scrolling effect
            const loadMore = () => {
                if (state.isIntersecting) {
                    state.page++; // Increment the current page
                    // Call your API or fetch data here to load more content
                }
            };

            // Add a scroll event listener to the scroll container
            const handleScroll = () => {
                const scrollHeight = scrollContainer.value.scrollHeight;
                const scrollTop = scrollContainer.value.scrollTop;
                const clientHeight = scrollContainer.value.clientHeight;

                // Check if the user has scrolled to the bottom of the container
                if (scrollHeight - scrollTop === clientHeight) {
                    loadMore(); // Trigger the infinite scroll effect
                }
            };

            return {
                scrollContainer,
                handleScroll,
            };
        },
    };
    </script>

