const express = require('express');
const path = require('path');
const fs = require('fs');
const ejs = require('ejs');

const app = express();

// Define the directory to scan
const directoryPath = './public';

// Define the EJS template
const template = `
<!DOCTYPE html>
<html>
  <head>
    <meta charset="UTF-8">
    <title>Directory Listing</title>
  </head>
  <body>
    <h1>Directory Listing</h1>
    <ul>
      <% for (var i = 0; i < files.length; i++) { %>
        <% var file = files[i]; %>
        <% if (file.isDirectory) { %>
          <li><%= file.name %></li>
          <ul>
            <% for (var j = 0; j < file.files.length; j++) { %>
              <% var subFile = file.files[j]; %>
              <li><%= subFile %></li>
            <% } %>
          </ul>
        <% } else { %>
          <li><%= file.name %></li>
        <% } %>
      <% } %>
    </ul>
  </body>
</html>
`;

// Define the function to scan the directory recursively
function scanDirectory(directoryPath) {
  const files = [];
  const fileNames = fs.readdirSync(directoryPath);
  for (let i = 0; i < fileNames.length; i++) {
    const fileName = fileNames[i];
    const filePath = path.join(directoryPath, fileName);
    const stats = fs.statSync(filePath);
    if (stats.isDirectory()) {
      const nestedFiles = scanDirectory(filePath);
      files.push({ name: fileName, isDirectory: true, files: nestedFiles });
    } else {
      files.push({ name: fileName, isDirectory: false });
    }
  }
  return files;
}

// Define the route to serve the directory listing
app.get('/', function(req, res) {
  const files = scanDirectory(directoryPath);
  const html = ejs.render(template, { files: files });
  res.send(html);
});

// Start the server
app.listen(3000, function() {
  console.log('Server listening on port 3000.');
});

