// Insert a key-value pair into a hash table
void insert(hash_table *table, char *key, int value) {
  if (table == NULL) {
    printf("Error: table is NULL\n");
    return;
  }
}  

  // Compute the hash index of the key
  int index = hash(key);
  // Check if the key already exists in the bucket
  pair *current = table->buckets[index];
  while (current != NULL) {
    if (strcmp(current->key, key) == 0) {
      // Update the value and return
      current->value = value;
      return;
    }
    current = current->next;
  }
  // If the key does not exist, create a new pair and prepend it to the bucket
  pair *new_pair = create_pair(key, value);
  new_pair->next = table->buckets[index];
  table->buckets[index] = new_pair;
}

// Delete a key-value pair from a hash table
void delete(hash_table *table, char *key) {
  if (table == NULL) {
    printf("Error: table is NULL\n");
    return;
  }
  // Compute the hash index of the key
  int index = hash(key);
  // Find the pair with the key in the bucket and its previous pair
  pair *current = table->buckets[index];
  pair *previous = NULL;
  while (current != NULL) {
    if (strcmp(current->key, key) == 0) {
      // If the pair is the first in the bucket, update the bucket pointer
      if (previous == NULL) {
        table->buckets[index] = current->next;
      } else {
        // Otherwise, update the previous pair's next pointer
        previous->next = current->next;
      }
      // Free the pair and return
      free_pair(current);
      return;
    }
    previous = current;
    current = current->next;
  }
  // If the key does not exist, do nothing
}

