  // Compute the hash index of the key
  int index = hash(key);
  // Find the pair with the key in the bucket
  pair *current = table->buckets[index];
  while (current != NULL) {
    if (strcmp(current->key, key) == 0) {
      // Return the value
      return current->value;
    }
