import React, { useState } from 'react';
import { ChromePicker } from 'react-color';

function DepthMapVisualizer(props) {
  const { depthMap } = props;

  const [colors, setColors] = useState([
    { r: 0, g: 0, b: 255 },
    { r: 0, g: 255, b: 0 },
    { r: 255, g: 255, b: 0 },
    { r: 255, g: 0, b: 0 }
  ]);

  const [displayColorPicker, setDisplayColorPicker] = useState(false);
  const [currentColor, setCurrentColor] = useState({ r: 0, g: 0, b: 255 });

  const getColor = (depth) => {
    if (depth < 0.25) {
      return `rgb(0, ${Math.floor(depth * 1020)}, 255)`;
    } else if (depth < 0.5) {
      return `rgb(0, 255, ${Math.floor(1020 - depth * 1020)})`;
    } else if (depth < 0.75) {
      return `rgb(${Math.floor(depth * 1020) - 255}, 255, 0)`;
    } else {
      return `rgb(255, ${Math.floor(1020 - depth * 1020)}, 0)`;
    }
  };

  const normalizeDepthMap = (depthMap) => {
    const height = depthMap.length;
    const width = depthMap[0].length;

    let maxDepth = Number.MIN_VALUE;
    let minDepth = Number.MAX_VALUE;

    for (let i = 0; i < height; i++) {
      for (let j = 0; j < width; j++) {
        maxDepth = Math.max(maxDepth, depthMap[i][j]);
        minDepth = Math.min(minDepth, depthMap[i][j]);
      }
    }

    let depthRange = maxDepth - minDepth;

    let normalizedDepthMap = [];

    for (let i = 0; i < height; i++) {
      let row = [];
      for (let j = 0; j < width; j++) {
        let depth = (depthMap[i][j] - minDepth) / depthRange;
        let color = getColor(depth);
        row.push(color);
      }
      normalizedDepthMap.push(row);
    }

    return normalizedDepthMap;
  };

  const handleColorMapSelection = (newColor) => {
    let newColors = [...colors];
    newColors.push(newColor.rgb);
    setColors(newColors);
    setDisplayColorPicker(false);
  };

  let normalizedDepthMap = normalizeDepthMap(depthMap);
  let height = normalizedDepthMap.length;
  let width = normalizedDepthMap[0].length;

  let colorBar = colors.map((color, index) => {
    return (
      <div key={color-${index}} style={{ backgroundColor: `rgb(${color.r},${color.g},${color.b})`, height: '20px', width: `${100 / colors.length}%` }}>
        <div style={{ textAlign: 'center' }}>{index}</div>
      </div>
    );
  });

  let imageRows = normalizedDepthMap.map((row, rowIndex) => {
    let imageCells = row.map((color, cellIndex) => {
      return (
        <td key={cell-${rowIndex}-${cellIndex}} style={{ backgroundColor: color,</tr>
);
});

let imageTable = (
<table>
<tbody>
{imageRows}
</tbody>
</table>
);

return (
<div>
<div style={{ display: ‘flex’, alignItems: ‘center’ }}>
<div style={{ marginRight: ‘10px’ }}>Depth</div>
{colorBar}
</div>
<div>
{imageTable}
</div>
<button onClick={handleColorMapSelection}>Select Color Map</button>
</div>
);
}

