def is_prime(number):
    if number > 1:
        for num in range(2, int(num ** 0.5) + 1):
            if number % num == 0:
                return False
        return True
    return False

def find_primes(limit):
    primes = []
    for num in range(2, limit):
        if is_prime(num):
            primes.append(num)
    return primes
