import java.awt.Color;
import java.util.Arrays;
import javax.swing.JFrame;
import org.math.plot.Plot2DPanel;

public class DepthMapVisualizer {
public static void visualizeDepthMap(double[][] depthMap) {
    // create plot panel
    Plot2DPanel plot = new Plot2DPanel();

    // set colormap
    Color[] colors = new Color[] { Color.BLUE, Color.GREEN, Color.YELLOW, Color.RED };
    double[] depths = new double[] { 0, 0.5, 1, 1.5 };
    plot.addLegend("DEPTH", Color.BLACK);
    plot.addLinePlot("", depths, colors);

    // add data to plot
    double[] x = new double[depthMap.length * depthMap[0].length];
    double[] y = new double[depthMap.length * depthMap[0].length];
    double[] z = new double[depthMap.length * depthMap[0].length];
    int k = 0;
    for (int i = 0; i < depthMap.length; i++) {
        for (int j = 0; j < depthMap[0].length; j++) {
            x[k] = i;
            y[k] = j;
            z[k] = depthMap[i][j];
            k++;
        }
    }
    plot.addScatterPlot("", x, y, z);

    // create frame and add plot
    JFrame frame = new JFrame("Depth Map");
    frame.setContentPane(plot);
    frame.pack();
    frame.setVisible(true);
}
}
```


