public class QueueUsingStacks {
    // Initialize two stacks: input_stack and output_stack
    private Stack<Integer> input_stack = new Stack<Integer>();
    private Stack<Integer> output_stack = new Stack<Integer>();

    public void enqueue(int item) {
        // Add elements to the input_stack
        input_stack.push(item);
    }

    public int dequeue() {
        // If the output_stack is empty, transfer all elements from the input_stack to the output_stack
        if (output_stack.isEmpty()) {
            while (!input_stack.isEmpty()) {
                output_stack.push(input_stack.pop());
            }
        }
        // Return the top element from the output_stack, which is the element at the front of the queue
        return output_stack.pop();
    }
}

