module keychain() {
  difference() {
    // create the keychain shape
    cube([keychain_width, keychain_width, keychain_thickness]);
    // extrude the text and position it on the keychain
    translate([keychain_width/2 - text_size/2, keychain_width/2 - text_size/2, keychain_thickness]) 
    linear_extrude(height = text_height, convexity = 10) 
    text("Hello, World!", size = text_size);
    // position the hanging loop at the top of the keychain
    translate([keychain_width/2, keychain_width/2, keychain_thickness - loop_thickness]) 
    hanging_loop();
  }
