// Example of the Maybe monad in JavaScript
class Maybe {
  constructor(value) {
    this.value = value;
  }
  static of(value) {
    return new Maybe(value);
  }
  map(fn) {
    return this.value ? Maybe.of(fn(this.value)) : Maybe.of(null);
  }
  join() {
    return this.value || Maybe.of(null);
  }
  chain(fn) {
    return this.map(fn).join();
  }
}
