```
// A hash table is a data structure that maps keys to values using a hash function
// A hash function is a function that takes a key and returns an integer index
// The index is used to store the key-value pair in an array of buckets
// Each bucket is a linked list of key-value pairs that have the same hash index
// This allows for handling collisions, which occur when two different keys have the same hash index

#include <stdio.h>
#include <stdlib.h>
#include <string.h>

// Define the size of the hash table array
#define TABLE_SIZE 10

// Define a structure for a key-value pair
typedef struct pair {
  char *key; // The key is a string
  int value; // The value is an integer
  struct pair *next; // The next pointer for the linked list
} pair;

// Define a structure for a hash table
typedef struct hash_table {
  pair **buckets; // The array of buckets
} hash_table;

// Create a new pair with the given key and value
pair *create_pair(char *key, int value) {
  // Allocate memory for the pair
  pair *new_pair = malloc(sizeof(pair));
  if (new_pair == NULL) {
    printf("Error: could not allocate memory for pair\n");
    exit(1);
  }
  // Copy the key string
  new_pair->key = strdup(key);
  if (new_pair->key == NULL) {
    printf("Error: could not allocate memory for key\n");
    exit(1);
  }
  // Set the value
  new_pair->value = value;
  // Set the next pointer to NULL
  new_pair->next = NULL;
  // Return the new pair
  return new_pair;
}

// Free the memory used by a pair
void free_pair(pair *p) {
  if (p != NULL) {
    // Free the key string
    free(p->key);
    // Free the pair itself
    free(p);
  }
}

// Create a new hash table with the given size
hash_table *create_table(int size) {
  // Allocate memory for the table
  hash_table *new_table = malloc(sizeof(hash_table));
  if (new_table == NULL) {
    printf("Error: could not allocate memory for table\n");
    exit(1);
  }
  // Allocate memory for the array of buckets
  new_table->buckets = malloc(sizeof(pair *) * size);
  if (new_table->buckets == NULL) {
    printf("Error: could not allocate memory for buckets\n");
    exit(1);
  }
  // Initialize all the buckets to NULL
  for (int i = 0; i < size; i++) {
    new_table->buckets[i] = NULL;
  }
  // Return the new table
  return new_table;
}

// Free the memory used by a hash table
void free_table(hash_table *table) {
  if (table != NULL) {
    // Free each bucket and its pairs
    for (int i = 0; i < TABLE_SIZE; i++) {
      pair *current = table->buckets[i];
      while (current != NULL) {
        pair *next = current->next;
        free_pair(current);
        current = next;
      }
    }
    // Free the array of buckets
    free(table->buckets);
    // Free the table itself
    free(table);
  }
}

// A simple hash function that uses the sum of ASCII values of the key characters modulo the table size
int hash(char *key) {
  int sum = 0;
  for (int i = 0; key[i] != '\0'; i++) {
    sum += key[i];
  }
  return sum % TABLE_SIZE;
}

// Insert a key-value pair into a hash table
void insert(hash_table *table, char *key, int value) {
  if (table == NULL) {
    printf("Error: table is NULL\n");
    return;
  }
}  

  // Compute the hash index of the key
  int index = hash(key);
  // Check if the key already exists in the bucket
  pair *current = table->buckets[index];
  while (current != NULL) {
    if (strcmp(current->key, key) == 0) {
      // Update the value and return
      current->value = value;
      return;
    }
    current = current->next;
  }
  // If the key does not exist, create a new pair and prepend it to the bucket
  pair *new_pair = create_pair(key, value);
  new_pair->next = table->buckets[index];
  table->buckets[index] = new_pair;
}

// Delete a key-value pair from a hash table
void delete(hash_table *table, char *key) {
  if (table == NULL) {
    printf("Error: table is NULL\n");
    return;
  }
  // Compute the hash index of the key
  int index = hash(key);
  // Find the pair with the key in the bucket and its previous pair
  pair *current = table->buckets[index];
  pair *previous = NULL;
  while (current != NULL) {
    if (strcmp(current->key, key) == 0) {
      // If the pair is the first in the bucket, update the bucket pointer
      if (previous == NULL) {
        table->buckets[index] = current->next;
      } else {
        // Otherwise, update the previous pair's next pointer
        previous->next = current->next;
      }
      // Free the pair and return
      free_pair(current);
      return;
    }
    previous = current;
    current = current->next;
  }
  // If the key does not exist, do nothing
}

// Search for a value associated with a key in a hash table
int search(hash_table *table, char *key) {
  if (table == NULL) {
    printf("Error: table is NULL\n");
    return -1;
  }
  // Compute the hash index of the key
  int index = hash(key);
  // Find the pair with the key in the bucket
  pair *current = table->buckets[index];
  while (current != NULL) {
    if (strcmp(current->key, key) == 0) {
      // Return the value
      return current->value;
    }
    current = current->next;
  }
  // If the key does not exist, return -1
  return -1;
}

// Print a hash table for debugging purposes
void print_table(hash_table *table) {
  if (table == NULL) {
    printf("Error: table is NULL\n");
    return;
  }
  // Loop through each bucket and print its pairs
  for (int i = 0; i < TABLE_SIZE; i++) {
    printf("Bucket %d: ", i);
    pair *current = table->buckets[i];
    while (current != NULL) {
      printf("(%s, %d) -> ", current->key, current->value);
      current = current->next;
    }
    printf("NULL\n");
  }
}

// A main function to test the hash table implementation
int main() {
  // Create a hash table
  hash_table *table = create_table(TABLE_SIZE);

  // Insert some key-value pairs
  insert(table, "Alice", 25);
  insert(table, "Bob", 32);
  insert(table, "Charlie", 28);
  insert(table, "David", 19);
  insert(table, "Eve", 23);

  // Print the table
  print_table(table);

  // Search for some keys
  printf("Value for Alice: %d\n", search(table, "Alice"));
  printf("Value for Bob: %d\n", search(table, "Bob"));
  printf("Value for Charlie: %d\n", search(table, "Charlie"));
  printf("Value for David: %d\n", search(table, "David"));
  printf("Value for Eve: %d\n", search(table, "Eve"));
  printf("Value for Frank: %d\n", search(table, "Frank"));

  // Delete some keys
  delete(table, "Alice");
  delete(table, "Bob");
  delete(table, "Charlie");

  // Print the table again
  print_table(table);

  // Free the table
  free_table(table);

  return 0;
}
```

