import React, { useState } from 'react';
import { useDrag, useDrop } from 'react-dnd';
import { ItemTypes } from './ItemTypes';

const style = {
  border: '1px solid gray',
  padding: '0.5rem 1rem',
  marginBottom: '.5rem',
  backgroundColor: 'white',
  cursor: 'move',
};

const Element = ({ id, text, moveElement }) => {
  const [{ isDragging }, drag] = useDrag({
    item: { type: ItemTypes.ELEMENT, id, text },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  });
  const [, drop] = useDrop({
    accept: ItemTypes.ELEMENT,
    drop: (item) => {
      moveElement(item.id, id);
    },
  });

  return (
    <div ref={(node) => drag(drop(node))} style={{ ...style, opacity: isDragging ? 0.5 : 1 }}>
      {text}
    </div>
  );
};

const Container = ({ elements, moveElement }) => {
  return (
    <div>
      {elements.map((element, index) => (
        <Element key={element.id} id={element.id} text={element.text} moveElement={moveElement} />
      ))}
    </div>
  );
};

