    public static void main(String[] args) throws DocumentClientException, InterruptedException {
        DocumentClient documentClient = new DocumentClient(ENDPOINT_URI, PRIMARY_KEY, ConnectionPolicy.GetDefault(), ConsistencyLevel.Session);

        FeedOptions feedOptions = new FeedOptions();
