        // Query the collection for partition key ranges
        FeedResponse<PartitionKeyRange> response = documentClient.readPartitionKeyRangeFeed(
                String.format("/dbs/%s/colls/%s/pkranges", DATABASE_NAME, COLLECTION_NAME), feedOptions);

        // Create the ChangeFeedProcessor instance
        ChangeFeedProcessor changeFeedProcessor = new ChangeFeedProcessorBuilder()
                .hostName("your-change-feed-processor-host-name")
                .feedCollection(documentClient, DATABASE_NAME, COLLECTION_NAME)
                .checkpointStoreCollection(documentClient, DATABASE_NAME, "your-checkpoint-store-collection-name")
                .handleChanges((docs, context) -> {
                    for (Document doc : docs) {
                        System.out.println("Received document change: " + doc.toJson());
                    }
                })
                .build();

        // Start the change feed processor
        changeFeedProcessor.start();
    }
}

