#include <stdio.h>
#include <stdlib.h>
#include <pthread.h>
#include <semaphore.h>

#define MAX_THREADS 2

int count = 0;
sem_t sem;
pthread_mutex_t mutex;
pthread_cond_t cond;

void *thread_function(void *arg)
{
    int thread_id = *((int *) arg);

    pthread_mutex_lock(&mutex); // lock mutex

    while (count < 10) {
        while (count % MAX_THREADS != thread_id) { // check condition
            pthread_cond_wait(&cond, &mutex); // wait for signal
        }

        // critical section
        printf(“Thread %d: count = %d\n”, thread_id, count);
        count++;
        sem_post(&sem); // signal the waiting thread
    }

    pthread_mutex_unlock(&mutex); // unlock mutex
}

int main()
{
    int i;
    pthread_t threads[MAX_THREADS];

    sem_init(&sem, 0, 0); // initialize semaphore
    pthread_mutex_init(&mutex, NULL); // initialize mutex
    pthread_cond_init(&cond, NULL); // initialize conditional variable

    int arg;
    for (i = 0; i < MAX_THREADS; i++) {
        arg = (int*) malloc(sizeof(*arg));
        *arg = i;
        pthread_create(&threads[i], NULL, thread_function, arg); // create threads
    }

    for (i = 0; i < MAX_THREADS; i++) {
        pthread_join(threads[i], NULL); // join threads
    }

    sem_destroy(&sem); // destroy semaphore
    pthread_mutex_destroy(&mutex); // destroy mutex
    pthread_cond_destroy(&cond); // destroy conditional variable

    return 0;
}
