#include <pthread.h>
#include <semaphore.h>

/* Struct for conditional variable */
typedef struct {
   sem_t sem;
   pthread_mutex_t mutex;
   int value;
} cond_var_t;

/* Initialize the conditional variable */
void cond_var_init(cond_var_t* cv) {
   sem_init(&cv->sem, 0, 0);
   pthread_mutex_init(&cv->mutex, NULL);
   cv->value = 0;
}

/* Wait on the conditional variable */
void cond_var_wait(cond_var_t* cv, pthread_mutex_t* mutex) {
   pthread_mutex_lock(&cv->mutex);
   cv->value++;
   pthread_mutex_unlock(mutex);
   sem_wait(&cv->sem);
   sem_post(&cv->sem);
}

/* Signal the conditional variable */
void cond_var_signal(cond_var_t* cv) {
   pthread_mutex_lock(&cv->mutex);
   if (cv->value > 0) {
      cv->value--;
      sem_post(&cv->sem);
   }
   pthread_mutex_unlock(&cv->mutex);
}

/* Destroy the conditional variable */
void cond_var_destroy(cond_var_t* cv) {
   sem_destroy(&cv->sem);
   pthread_mutex_destroy(&cv->mutex);
}
```

