def TribRec(n):
    if n in {0, 1, 2}:
        return n
    else:
        return TribRec(n-1) + TribRec(n-2) + TribRec(n-3)

def Trib(n):
    for i in range(0, n):
        yield TribRec(i)

res = list(Trib(10))
