Sure, here's an example Terraform HCL code that creates an AWS Autoscaling Group and an Application Load Balancer to expose an application to the internet:
``` 
# Configure the AWS provider
provider "aws" {
  region = "us-east-1"
}

# Create a security group to allow traffic to the ALB
resource "aws_security_group" "alb_sg" {
  name_prefix = "alb_sg"
  ingress {
    from_port = 80
    to_port = 80
    protocol = "tcp"
    cidr_blocks = ["0.0.0.0/0"]
  }
}

# Create an ALB and target group
resource "aws_lb" "alb" {
  name               = "example-alb"
  internal           = false
  load_balancer_type = "application"

  subnets = ["subnet-12345678", "subnet-87654321"]

  security_groups = [aws_security_group.alb_sg.id]

  tags = {
    Environment = "production"
  }
}

resource "aws_lb_target_group" "target_group" {
  name        = "example-target-group"
  port        = 80
  protocol    = "HTTP"
  vpc_id      = "vpc-12345678"

  health_check {
    path = "/health"
  }

  tags = {
    Environment = "production"
  }
}

# Create an autoscaling group
resource "aws_launch_configuration" "launch_configuration" {
  name_prefix   = "example-lc-"
  image_id      = "ami-12345678"
  instance_type = "t2.micro"

  # Other settings here (e.g. security groups, userdata, etc.)
}

resource "aws_autoscaling_group" "autoscaling_group" {
  name                      = "example-asg"
  launch_configuration      = aws_launch_configuration.launch_configuration.id
  target_group_arns         = [aws_lb_target_group.target_group.arn]
  health_check_type         = "EC2"
  min_size                  = 1
  max_size                  = 3
  desired_capacity          = 2
  vpc_zone_identifier       = ["subnet-12345678", "subnet-87654321"]
  termination_policies      = ["Default"]
  wait_for_capacity_timeout = "10m"

  tags = {
    Environment = "production"
  }
}
