import java.util.ArrayList;
import java.util.Random;

public class DungeonMapGenerator {

    private int numRooms;
    private int maxAttempts;
    private ArrayList<Room> rooms;
    private Random random;

    public DungeonMapGenerator(int numRooms, int maxAttempts) {
        this.numRooms = numRooms;
        this.maxAttempts = maxAttempts;
        this.rooms = new ArrayList<Room>();
        this.random = new Random();
    }

    public ArrayList<Room> generate() {
        Room firstRoom = new Room(random.nextInt(10), random.nextInt(10), random.nextInt(10), random.nextInt(10));
        rooms.add(firstRoom);

        for (int i = 1; i < numRooms; i++) {
            Wall wall = selectWall();
            Room room = selectRoom();
            Door door = new Door(random.nextInt(wall.getLength()), wall);
            room.addDoor(door);
            boolean success = false;
            int attempts = 0;
            while (!success && attempts < maxAttempts) {
                room.setPosition(random.nextInt(10), random.nextInt(10));
                success = !overlapExists(room);
                attempts++;
            }
            if (success) {
                rooms.add(room);
                alignRooms(room, wall, door);
            }
        }
        return rooms;
    }

    private Wall selectWall() {
        Room room = rooms.get(random.nextInt(rooms.size()));
        return room.getWalls().get(random.nextInt(room.getWalls().size()));
    }

    private Room selectRoom() {
        return new Room(random.nextInt(10), random.nextInt(10), random.nextInt(10), random.nextInt(10));
    }

    private boolean overlapExists(Room newRoom) {
        for (Room room : rooms) {
            if (room.overlaps(newRoom)) {
                return true;
            }
        }
        return false;
    }

    private void alignRooms(Room room, Wall wall, Door door) {
        switch (wall.getOrientation()) {
            case HORIZONTAL:
                if (door.getX() < room.getX()) {
                    room.move(room.getX() - door.getX(), 0);
                } else if (door.getX() > room.getX() + room.getWidth()) {
                    room.move(room.getX() + room.getWidth() - door.getX(), 0);
                }
                break;
            case VERTICAL:
                if (door.getY() < room.getY()) {
                    room.move(0, room.getY() - door.getY());
                } else if (door.getY() > room.getY() + room.getHeight()) {
                    room.move(0, room.getY() + room.getHeight() - door.getY());
                }
                break;
        }
    }

    private enum Orientation {
        HORIZONTAL,
        VERTICAL
    }

    private class Door {
        private int position;
        private Wall wall;

        public Door(int position, Wall wall) {
            this.position = position;
            this.wall = wall;
        }

        public int getX() {
            if (wall.getOrientation() == Orientation.HORIZONTAL) {
                return wall.getStartX() + position;
            } else {
                return wall.getStartX();
            }
        }

        public int getY() {
            if (wall.getOrientation() == Orientation.VERTICAL) {
                return wall.getStartY() + position;
            } else {
                return wall.getStartY();
            }
        }
    }

    private class Wall {
        private int startX;
        private int startY;
        private int length;
        private Orientation orientation;

        public Wall(int startX, int startY, int length, Orientation orientation) {
            this.startX = startX;
            this.startY = startY;
            this.length = length;
            this.orientation = orientation;
        }

        public int getStart    X() {
        return startX;
