}

private class Room {
    private int x;
    private int y;
    private int width;
    private int height;
    private ArrayList<Door> doors;
    private ArrayList<Wall> walls;

    public Room(int x, int y, int width, int height) {
        this.x = x;
        this.y = y;
        this.width = width;
        this.height = height;
        this.doors = new ArrayList<Door>();
        this.walls = new ArrayList<Wall>();
        walls.add(new Wall(x, y, width, Orientation.HORIZONTAL));
        walls.add(new Wall(x, y, height, Orientation.VERTICAL));
        walls.add(new Wall(x, y + height, width, Orientation.HORIZONTAL));
        walls.add(new Wall(x + width, y, height, Orientation.VERTICAL));
    }

    public int getX() {
        return x;
    }

    public int getY() {
        return y;
    }

    public int getWidth() {
        return width;
    }

    public int getHeight() {
        return height;
    }

    public ArrayList<Door> getDoors() {
        return doors;
    }

    public ArrayList<Wall> getWalls() {
        return walls;
    }

    public void addDoor(Door door) {
        doors.add(door);
    }

    public boolean overlaps(Room other) {
        return (x < other.x + other.width) && (x + width > other.x) && (y < other.y + other.height) && (y + height > other.y);
    }

    public void setPosition(int newX, int newY) {
        x = newX;
        y = newY;
        walls.get(0).startX = newX;
        walls.get(1).startY = newY;
        walls.get(2).startY = newY + height;
        walls.get(3).startX = newX + width;
        for (Door door : doors) {
            door.wall = getMatchingWall(door.wall);
        }
    }

    public void move(int dx, int dy) {
        setPosition(x + dx, y + dy);
    }

    private Wall getMatchingWall(Wall other) {
        for (Wall wall : walls) {
            if (wall.getOrientation() == other.getOrientation()) {
                switch (wall.getOrientation()) {
                    case HORIZONTAL:
                        if (wall.getStartY() == other.getStartY() && wall.getStartX() <= other.getStartX() && wall.getStartX() + wall.getLength() >= other.getStartX()) {
                            return wall;
                        }
                        break;
                    case VERTICAL:
                        if (wall.getStartX() == other.getStartX() && wall.getStartY() <= other.getStartY() && wall.getStartY() + wall.getLength() >= other.getStartY()) {
                            return wall;
                        }
                        break;
                }
            }
        }
        return null;
    }
}



