        grid.Attach(taskLabel, 0, 0, 1, 1);
        Entry taskEntry = new Entry();
        taskEntry.Name = "task_entry";
        grid.Attach(taskEntry, 1, 0, 1, 1);

        // Add due date label and entry to grid
        Label dueDateLabel = new Label("Due Date:");
        grid.Attach(dueDateLabel, 0, 1, 1, 1);
        Entry dueDateEntry = new Entry();
        dueDateEntry.Name = "due_date_entry";
        grid.Attach(dueDateEntry, 1, 1, 1, 1);

        // Add "Add Task" button to grid
        Button addButton = new Button("Add Task");
        addButton.Expand = false;
        addButton.Halign = Align.End;
        addButton.Valign = Align.End;
