      let max_tr_prob = V[0][t-1] * trans_p[0][j];
      let prev_state_selected = 0;
      for (let i = 1; i < states.length; i++) {
        let tr_prob = V[i][t-1] * trans_p[i][j];
        if (tr_prob > max_tr_prob) {
          max_tr_prob = tr_prob;
          prev_state_selected = i;
