      V[j][t] = max_tr_prob * emit_p[j][obs[t]];
    }
  }

  let opt = [];
  let max_prob = V[0][obs.length - 1];
  let state_selected = 0;

  for (let i = 1; i < states.length; i++) {
    if (V[i][obs.length - 1] > max_prob) {
      max_prob = V[i][obs.length - 1];
      state_selected = i;
    }
  }

