    for (let i = 0; i < states.length; i++) {
      if (V[i][t] * trans_p[i][state_selected] === V[state_selected][t + 1]) {
        state_selected = i;
      }
    }
    opt.unshift(state_selected);
  }

  return {
    prob: max_prob,
    path: opt
  };
