import os
import pickle
from torch.utils.data import Dataset, DataLoader

class BRUSHDataset(Dataset):
     def __init__(self, root_dir):
        self.root_dir = root_dir
        self.data = []

        for writer_id in os.listdir(self.root_dir):
            if os.path.isdir(os.path.join(self.root_dir, writer_id)):
                for drawing_id in os.listdir(os.path.join(self.root_dir, writer_id)):
                    with open(os.path.join(self.root_dir, writer_id, drawing_id), 'rb') as f:
                        sentence, drawing, label = pickle.load(f)
                        self.data.append((sentence, drawing, label))

    def __len__(self):
        return len(self.data)

    def __getitem__(self, idx):
        sentence, drawing, label = self.data[idx]
        return sentence, drawing, label

# Usage example
dataset = BRUSHDataset("data/BRUSH")
dataloader = DataLoader(dataset, batch_size=32, shuffle=True)

for sentences, drawings, labels in dataloader:
    # do something with the batch of data
