pragma solidity ^0.8.0;

contract RazorScooterSale {
    address payable public seller;
    address payable public buyer;
    uint public price;
    bool public sold;

    constructor() {
        seller = payable(msg.sender);
        price = 1 ether; // Set the price to 1 ether
        sold = false;
    }

    function buy() public payable {
        require(msg.value == price, "Price must be exactly 1 ether"); // Check that the buyer has sent the correct amount of ether
        require(!sold, "The scooter has already been sold"); // Check that the scooter has not already been sold
        buyer = payable(msg.sender);
        seller.transfer(msg.value);
        sold = true;
    }
}
```

