import java.awt.Color;
import java.awt.image.BufferedImage;
import java.io.File;
import java.io.IOException;
import javax.imageio.ImageIO;

public class DepthMapVisualizer {
public static void visualizeDepthMap(double[][] depthMap) throws IOException {
// create image
int width = depthMap[0].length;
int height = depthMap.length;
BufferedImage image = new BufferedImage(width, height, BufferedImage.TYPE_INT_RGB);
    // create color map
    double minDepth = Double.POSITIVE_INFINITY;
    double maxDepth = Double.NEGATIVE_INFINITY;
    for (int y = 0; y < height; y++) {
        for (int x = 0; x < width; x++) {
            double depth = depthMap[y][x];
            if (depth < minDepth) {
                minDepth = depth;
            }
            if (depth > maxDepth) {
                maxDepth = depth;
            }
        }
    }
    double depthRange = maxDepth - minDepth;
    Color[] colorMap = new Color[256];
    for (int i = 0; i < 256; i++) {
        double depth = minDepth + (i / 255.0) * depthRange;
        colorMap[i] = getColorForDepth(depth);
    }

    // fill image with colors
    for (int y = 0; y < height; y++) {
        for (int x = 0; x < width; x++) {
            double depth = depthMap[y][x];
            int colorIndex = (int) Math.round((depth - minDepth) / depthRange * 255);
            Color color = colorMap[colorIndex];
            image.setRGB(x, y, color.getRGB());
        }
    }

    // save image
    File output = new File("depth_map.png");
    ImageIO.write(image, "png", output);
}

private static Color getColorForDepth(double depth) {
    // define color scheme here
    // return color based on depth
    return Color.getHSBColor(0.5f, 1.0f, 1.0f);
}
}
