public class DepthMapVisualizer {

public static void visualizeDepthMap(double[][] depthMap) {
    int width = depthMap[0].length;
    int height = depthMap.length;

    BufferedImage image = new BufferedImage(width, height, BufferedImage.TYPE_INT_RGB);

    // normalize depth map values to range [0, 255]
    double maxDepth = Double.MIN_VALUE;
    double minDepth = Double.MAX_VALUE;
    for (int i = 0; i < height; i++) {
        for (int j = 0; j < width; j++) {
            maxDepth = Math.max(maxDepth, depthMap[i][j]);
            minDepth = Math.min(minDepth, depthMap[i][j]);
        }
    }
    double depthRange = maxDepth - minDepth;
    for (int i = 0; i < height; i++) {
        for (int j = 0; j < width; j++) {
            double depth = (depthMap[i][j] - minDepth) / depthRange;
            Color color = getColor(depth);
            image.setRGB(j, i, color.getRGB());
        }
    }

    // create JFrame to display image
    JFrame frame = new JFrame("Depth Map");

    // create JPanel to display color bar
    JPanel colorBarPanel = new JPanel();
    colorBarPanel.setBackground(Color.white);
    JLabel colorBarLabel = new JLabel("Depth");
    colorBarPanel.add(colorBarLabel);

