    int nColors = colors.length;
    for (int i = 0; i < nColors; i++) {
        JPanel colorPanel = new JPanel();
        colorPanel.setBackground(colors[i]);
        colorBarPanel.add(colorPanel);
        double depth = minDepth + (i * depthRange / (nColors - 1));
        JLabel depthLabel = new JLabel(String.format("%.2f", depth));
        colorBarPanel.add(depthLabel);
    }

    // create JLabel with image
    JLabel imageLabel = new JLabel(new ImageIcon(image));

    // create JPanel to hold image and color bar
    JPanel panel = new JPanel();
    panel.add(imageLabel);
    panel.add(colorBarPanel);

    // add panel to JFrame
    frame.getContentPane().add(panel);

    // show JFrame
    frame.pack();
    frame.setVisible(true);
}

private static Color getColor(double depth) {
    if (depth < 0.25) {
        return new Color(0, (int)(depth * 1020), 255);
    } else if (depth < 0.5) {
        return new Color(0, 255, 1020 - (int)(depth * 1020));
    } else if (depth < 0.75) {
        return new Color((int)(depth * 1020) - 255, 255, 0);
    } else {
        return new Color(255, 1020 - (int)(depth * 1020), 0);
    }
}
}
